hudson.tasks.Shell.command:
#!c:\cygwin_aclnas03\bin\bash --login
User=$ARTIFACTORY_USER:$ARTIFACTORY_PASSWORD curl=c:/curl/curl # Calculate the date we are going to delete oldThan=$(date +"%Y%m%d" -d "-$OLD days") moveDone=0
cd $WORKSPACE
function RemoveEachFolder() {
local RemoveFolder=$1 local TargetPath=$2 $curl -ks -X GET https://$ARTIFACTORY/api/storage/$RemoveFolder | grep uri | grep -v https: | sort -V | while read line do uri=`echo $line | cut -d'"' -f4` DestSize=0; ArtSize=0 ArtSize=`$curl -ks -X GET https://$ARTIFACTORY/api/storage/${RemoveFolder}$uri | grep size | cut -d: -f2 | cut -d'"' -f2` if [ "$ArtSize" != "" ]; then echo Download file ${RemoveFolder}$uri to "${DESTINATION}/${TargetPath}$uri"... $curl -ks -u $User -X GET https://$ARTIFACTORY/${RemoveFolder}$uri --output "${DESTINATION}/${TargetPath}$uri" DestSize=`wc -c <"${DESTINATION}/${TargetPath}$uri"` if [ "$ArtSize" != "$DestSize" ]; then echo $ArtSize--$DestSize: $file is differ - No delete and exit...... exit 1 else echo Size equel - delete from Artifactory... $curl -ks -u $User -X DELETE https://$ARTIFACTORY/${RemoveFolder}$uri fi else echo -- mkdir "${DESTINATION}/${TargetPath}$uri" mkdir "${DESTINATION}/${TargetPath}$uri" RemoveEachFolder ${RemoveFolder}$uri "${TargetPath}$uri" if $curl -ks -X GET https://$ARTIFACTORY/api/storage/${RemoveFolder}$uri | grep -qF '"children" : [ ],'; then echo ----- Delete the empty directory from Artifactory ${RemoveFolder}$uri... $curl -ks -u $User -X DELETE https://$ARTIFACTORY/${RemoveFolder}$uri fi fi done }
# this is a recursive function - check the last modified date, if old or equel to $oldThan than remove this folder from the Artifactory function CheckFolder() {
local Folder=$1 local Target=$2 # The date format is: YYYYMMDD (Y=the year, M=Month, D=Day - Example: 8 Jan 2018 should be 20180108 lastModified=$($curl -ks -X GET https://$ARTIFACTORY/api/storage/$Folder?lastModified | grep lastModified | cut -d'"' -f4 | cut -dT -f1 | sed s:-::g)
echo "$lastModified -le $oldThan" if [ "$lastModified" -le "$oldThan" ]; then echo move $Folder echo --------------------- mkdir "${DESTINATION}/$Target" RemoveEachFolder $Folder "$Target" if $curl -ks -X GET https://$ARTIFACTORY/api/storage/$Folder | grep -qF '"children" : [ ],'; then echo ----- Delete the empty directory from Artifactory $Folder... $curl -ks -u $User -X DELETE https://$ARTIFACTORY/$Folder fi return 1 else echo $Folder is not old - check its sub folders $curl -ks -X GET https://$ARTIFACTORY/api/storage/$Folder | grep -B 1 -E 'folder.*true' | grep uri | cut -d'"' -f4 | sort -V | while read uri do echo Execute this function again to check the following directory: ${Folder}$uri lastMovedone=$moveDone CheckFolder ${Folder}$uri "${Target}$uri" moveDone=$? if [ $moveDone -eq 0 ]; then echo ---------- Probably all next folder are not older... break from this folder check ------------ break fi done return 0 fi }
DESTINATION=$(echo $DESTINATION | sed -e 's:\\:/:g')
ARTIFACTORY=test-artifactory CheckFolder sbc-release/com/ac/sbc/7.2.150/Firmware "7.2.150 Release/Firmware" CheckFolder sbc-release/com/ac/sbc/7.2.154/Firmware "7.2.150 Release/7.2.154" CheckFolder sbc-release/com/ac/sbc/7.2.156/Firmware "7.2.150 Release/7.2.156"
