definition.script:
pipeline {
    agent {
        node {
            label 'ems-build'
            customWorkspace '/jenkins/workspace'
        }
    }
    parameters {
        string(name: 'NEW_TAG', defaultValue: '')
        string(name: 'OLD_TAG', defaultValue: '')
        choice(name: 'DeployISO', choices: ['official_iso', 'latest_iso'])
        string(name: 'MASTER_IP', defaultValue: '')
        string(name: 'VQ_IP', defaultValue: '')
        string(name: 'PM_IP', defaultValue: '')
    }
    stages {
        stage('Install Nodes'){
            parallel {
                stage('Install master') {
                    steps {
                        build job: 'OVOC-Cluster-DVD-Install', parameters: [
                            string(name: 'TAG', value: "${OLD_TAG}"),
                            string(name: 'ServerRole', value: 'Master'),
                            string(name: 'ServerIP', value: "${MASTER_IP}"),
                            string(name: 'DeployISO', value: "${DeployISO}")
                        ]
                        build job: 'OVOC-Cluster-DVD-Install', parameters: [
                            string(name: 'TAG', value: "${NEW_TAG}"),
                            string(name: 'ServerRole', value: 'Master'),
                            string(name: 'ServerIP', value: "${MASTER_IP}"),
                            string(name: 'DeployISO', value: "${DeployISO}")
                        ]
                    }
                }
                stage('Install VQ') {
                    steps {
                        build job: 'OVOC-Cluster-DVD-Install', parameters: [
                            string(name: 'TAG', value: "${OLD_TAG}"),
                            string(name: 'ServerRole', value: 'VQ'),
                            string(name: 'ServerIP', value: "${VQ_IP}"),
                            string(name: 'DeployISO', value: "${DeployISO}")
                        ]
                        build job: 'OVOC-Cluster-DVD-Install', parameters: [
                            string(name: 'TAG', value: "${NEW_TAG}"),
                            string(name: 'ServerRole', value: 'VQ'),
                            string(name: 'ServerIP', value: "${VQ_IP}"),
                            string(name: 'DeployISO', value: "${DeployISO}")
                        ]
                    }
                }
                stage('Install PM') {
                    steps {
                        build job: 'OVOC-Cluster-DVD-Install', parameters: [
                            string(name: 'TAG', value: "${OLD_TAG}"),
                            string(name: 'ServerRole', value: 'PM'),
                            string(name: 'ServerIP', value: "${PM_IP}"),
                            string(name: 'DeployISO', value: "${DeployISO}")
                        ]
                        build job: 'OVOC-Cluster-DVD-Install', parameters: [
                            string(name: 'TAG', value: "${NEW_TAG}"),
                            string(name: 'ServerRole', value: 'PM'),
                            string(name: 'ServerIP', value: "${PM_IP}"),
                            string(name: 'DeployISO', value: "${DeployISO}")
                        ]
                    }
                }
            }
        }
    }
}
