hudson.tasks.Shell.command:
### Install yq - this is temporary solution, should be installed in AMI!!! #curl -sL -o https://github.com/mikefarah/yq/releases/download/2.4.1/yq_linux_amd64 && \ # sudo mv yq_linux_amd64 /usr/local/bin/yq && \ # sudo chmod +x /usr/local/bin/yq
## Set branch by $version case $version in 1.1*|1.2*) BRANCH="1.2" ;; 1.3*|1.4*) BRANCH="1.4" ;; 1.5*|1.6*) BRANCH="1.6" ;; 1.7*|1.8*) BRANCH="1.8" ;; *) echo "Missing version parameter!!" exit 1 ;; esac
### Build the project ## Set docker vars ART_DOCKER_REG="docker-voice-ai.artifactory.corp.audiocodes.com" ECR_DOCKER_REG="858367874798.dkr.ecr.eu-central-1.amazonaws.com" ECR_DOCKER_PREFIX="voice-ai"
## Login to docker registry on Artifactory echo ${jenkinsPass} | docker login -u jenkins --password-stdin ${ART_DOCKER_REG}
## Login to ECR $(aws ecr get-login --no-include-email --region eu-central-1) || exit 1
## Download version YAML file curl -O https://artifactory:443/docker-voice-ai-files/docker-compose-vag-$version.yml #curl -O https://artifactory:443/docker-voice-ai-files/docker-compose-vag-$version-sm.yml #curl -O https://artifactory:443/docker-voice-ai-files/docker-compose-vag-$version-central.yml #curl -O https://artifactory:443/docker-voice-ai-files/docker-compose-vag-$version-azure-mock.yml
## Get images and versions images=$(grep "image: docker-voice-ai" docker-compose-vag-$version.yml | cut -d/ -f2)
for img in ${images}; do image=$(echo $img | cut -d: -f1) tag=$(echo $img | cut -d: -f2)
## Create remote repository if not exist awsCommand="aws ecr describe-repositories --repository-name ${ECR_DOCKER_PREFIX}/${image}" if [[ ! $($awsCommand) ]]; then echo "*** Creating remote repository ${ECR_DOCKER_PREFIX}/${image}" aws ecr create-repository --repository-name "${ECR_DOCKER_PREFIX}/${image}" fi
## Pull, tag and push image to remote repository # Image is pushed to ECR once with the tag it was pulled from Artifactory # and once with YAML version tag docker pull ${ART_DOCKER_REG}/"${image}:${tag}" docker tag ${ART_DOCKER_REG}/"${image}:${tag}" ${ECR_DOCKER_REG}/${ECR_DOCKER_PREFIX}/"${image}:${tag}" docker tag ${ART_DOCKER_REG}/"${image}:${tag}" ${ECR_DOCKER_REG}/${ECR_DOCKER_PREFIX}/"${image}:${version}" docker push ${ECR_DOCKER_REG}/${ECR_DOCKER_PREFIX}/"${image}:${tag}" docker push ${ECR_DOCKER_REG}/${ECR_DOCKER_PREFIX}/"${image}:${version}" done
## Create YAMLs for ECR #Basicaly this script does the following: #1. Create an empty `ecr` directory #2. Replace URI `docker-voice-ai.artifactory.corp.audiocodes.com` # with `858367874798.dkr.ecr.eu-central-1.amazonaws.com/voice-ai` #3. Remove `ports` section from mongo & influxdb images on the combined yaml. #4. Put the new file in `ecr` directory. # cd ${WORKSPACE}
[[ "$version" == "${BRANCH}" ]] && git checkout -f origin/$version -- common/ECR-yaml-manipulation.sh || git checkout -f $version -- common/ECR-yaml-manipulation.sh . common/ECR-yaml-manipulation.sh # ## Copy YAML files to S3 aws s3 sync "ecr" s3://voice-ai/docker-compose/${version}
## Properties to export to environment echo VERSION=$version > VAG.properties echo IMAGES=${images} >> VAG.properties cat VAG.properties
