################################################################################################### # Check & delete old ovoc servers
# check and delete old ovoc server vm`s SSHCMD="powershell -c 'Get-VM -VMName \"*ovoc*\" | %{Stop-VM \$_}'" echo $SSHCMD | ssh centos@${vmHostIP} SSHCMD="powershell -c 'Get-VM -VMName \"*ovoc*\" | %{Remove-VM -f \$_}'" echo $SSHCMD | ssh centos@${vmHostIP}
# Delete old remnants from hypervisor host SSHCMD="powershell -c 'Remove-Item -Force -Recurse V:\\HVWORKSPACE\\*'" echo $SSHCMD | ssh centos@${vmHostIP} SSHCMD="powershell -c 'Remove-Item -Force -Recurse V:\\Exports\\*'" echo $SSHCMD | ssh centos@${vmHostIP} SSHCMD="powershell -c 'Remove-Item -Force -Recurse V:\\Versions\\*'" echo $SSHCMD | ssh centos@${vmHostIP}
################################################################################################### # Prepare basic ovoc server (DVD 1 & 2)VM from BaseVM
# UnZip archived base vm SSHCMD="powershell -c 'Expand-7zip -ArchiveFileName V:\\baseVMs\\$BaseVM.7z -TargetPath V:\\HVWORKSPACE\\${TAG}'" echo $SSHCMD | ssh centos@${vmHostIP}
# create a new VM from $BaseVM (VM files are under v:\base (/cygdrive/v/base)) echo "$(date) : creating new base ovoc server VM from $BaseVM" SSHCMD="powershell -c 'Get-ChildItem -Recurse V:\\HVWORKSPACE\\${TAG} -Filter *.vmcx | Select -ExpandProperty FullName | Import-VM -Register'" echo $SSHCMD | ssh centos@${vmHostIP} SSHCMD="powershell -c 'Get-VM -VMName \"*ovoc*\" | Set-VMMemory -DynamicMemoryEnabled \$false'" echo $SSHCMD | ssh centos@${vmHostIP} SSHCMD="powershell -c 'Get-VM -VMName \"*ovoc*\" | Start-VM'" echo $SSHCMD | ssh centos@${vmHostIP}
if [ $? -ne 0 ]; then echo "$(date) : Deploy new VM has failed - check ems_ovoc_vm_log.out" exit fi
# Wait for ovoc server power on #isUp $vmHostIP $vmid || exit echo "$(date) : sleep 5 min." sleep 300
# Update ovoc server /etc/hosts file with DevOps servers sshpass -p "$ems_pass" ssh -o StrictHostKeyChecking=no acems@${vmip} su - << EOF $su_pass #cp /etc/hosts /etc/hosts.keep #echo -e "172.16.133.170\tartifactory\n172.16.143.99\ttest-artifactory\n172.16.146.146\tjenkins\n172.16.143.181\ttest-jenkins" >> /etc/hosts echo -e "search corp.audiocodes.com\nnameserver 10.1.1.10\nnameserver 10.1.1.6" > /etc/resolv.conf EOF
################################################################################################### # Install DVD 3 on basic vovoc server
# if branch is 7.4 then we dont run this step # in branch is higher than 7.4 then we need to arrange the directory EMS/target # so it will contain only the 5 jar files we copied from build-ovoc job archive # and all executables cd ${WORKSPACE}/EMS [[ ! -f target ]] && mkdir target chmod 0775 target cd target rm -rf * cp ${WORKSPACE}/ovaPreRequisites/* . chmod 0775 * cd ${WORKSPACE}
