# setNetwork.py for version 7.6 and later calculate netmask by itself (python2.7 setNetwork.py $vmip sanity) || (echo "$(date) : Failed network setting" && exit -1)
cd ${WORKSPACE}
# ovoc server was rebooted, so - # Wait for ovoc server power on #isUp ${vmHostIP} $vmid || exit -2 echo "$(date) : sleep 5 min." sleep 300
# Sanity Install DVD-3 cd EMS/VersionRelease (python2.7 SanityInstall.py ${TAG} $vmip official_iso) || (echo "$(date) : Failed sanity install" && exit -1) cd ${WORKSPACE} echo "$(date) : Sleep 30" sleep 30 echo "###################################################################################################" echo "###################################################################################################" echo "###################################################################################################" echo "$(date) : Done applying DVD 3" echo "###################################################################################################" echo "###################################################################################################" echo "###################################################################################################"
# ovoc server was rebooted during DVD 3 installation, so - # Wait for ovoc server power on #isUp ${vmHostIP} $vmid || exit -2 echo "$(date) : sleep 5 min." sleep 300
################################################################################################### # clean before export to BaseVM echo "$(date) : ssh to clean installetion files"
sshpass -p "$ems_pass" ssh -o StrictHostKeyChecking=no acems@${vmip} su - << EOF $su_pass echo "" > /etc/resolv.conf EOF
cd EMS/VersionRelease (python2.7 cleanBeforeOva.py $vmip) || (echo "$(date) : Failed cleaning installation files" && exit -1) cd ${WORKSPACE} echo "$(date) : Sleep 30" sleep 30
################################################################################################### # graceful shutdouwn this new full ovoc server VM and rename with TAG echo "$(date) : shutdown new VM" SSHCMD="powershell -c 'Get-VM -VMName \"*ovoc*\" | Stop-VM'" echo $SSHCMD | ssh centos@${vmHostIP} SSHCMD="powershell -c 'Get-VM -VMName \"*ovoc*\" | Rename-VM -NewName ovoc-${TAG}'" echo $SSHCMD | ssh centos@${vmHostIP} echo "$(date) : Sleep 30" sleep 30
################################################################################################### # export the new VM and compress echo "$(date) : Verify no version ${TAG} exists" SSHCMD="powershell -c 'if (Test-Path V:\\Exports\\${TAG}) {Remove-Item -Recurse V:\\Exports\\${TAG}\\*} else {New-Item -ItemType Directory V:\\Exports\\${TAG}}' " echo $SSHCMD | ssh centos@${vmHostIP} SSHCMD="powershell -c 'if (Test-Path V:\\Versions\\${TAG}) {Remove-Item -Recurse V:\\Versions\\${TAG}\\*} else {New-Item -ItemType Directory V:\\Versions\\${TAG}}' " echo $SSHCMD | ssh centos@${vmHostIP}
echo "$(date) : export new VM" SSHCMD="powershell -c 'Get-VM -VMName \"*ovoc*\" | Export-VM -Path V:\\Exports\\${TAG}'" echo $SSHCMD | ssh centos@${vmHostIP}
echo "$(date) : Compress exported VM" SSHCMD="powershell -c 'Compress-7Zip -ArchiveFileName V:\\Versions\\${TAG}\\OVOC_HyperV-${HyperV}_${TAG}.7z -Path V:\\Exports\\${TAG}\\ovoc-${TAG}'" echo $SSHCMD | ssh centos@${vmHostIP}
echo "$(date) : Calculate md5" SSHCMD="powershell -c 'Get-FileHash -Algorithm md5 V:\\Versions\\${TAG}\\OVOC_HyperV-${HyperV}_${TAG}.7z | select -ExpandProperty Hash > V:\\Versions\\${TAG}\\OVOC_HyperV-${HyperV}_${TAG}.md5'" echo $SSHCMD | ssh centos@${vmHostIP}
#[[ ! -f /cygdrive/v/Exports/OVOC_HyperV-${HyperV}_${TAG}.7z ]] && exit
echo "###################################################################################################" echo "###################################################################################################" echo "###################################################################################################" echo "$(date) : Done export new VM" echo "###################################################################################################" echo "###################################################################################################" echo "###################################################################################################"
