# Update ovoc server /etc/hosts file with DevOps servers (Here document must not be indented) sshpass -p "$ems_pass" ssh -o StrictHostKeyChecking=no acems@${vmip} su - << EOF $su_pass echo -e "search corp.audiocodes.com\nnameserver 10.1.1.10\nnameserver 10.1.1.6" > /etc/resolv.conf EOF
################################################################################################### # Generate and install FK echo "$(date) : Generate FK" cd EMS/VersionRelease (python2.7 SanityFK.py $vmip $TAG) || (echo "$(date) : Failed FK" && exit -1) cd ${WORKSPACE}
################################################################################################### # Reboot & Verify services on ovoc_server after FK installation echo "$(date) : Reboot & Verify services on ovoc_server after FK installation" cd EMS/VersionRelease (python2.7 SanityReboot.py $vmip) || (echo "$(date) : Failed reboot after FK installation" && exit -1) cd ${WORKSPACE} echo "$(date) : Sleep 300" sleep 300
################################################################################################### # Run sanity tests on ovoc server echo "$(date) : Run sanity tests on ovoc server" cd EMS/VersionRelease FullTesting="1" python2.7 SanityTest.py ${TAG} $vmip $FullTesting || echo "$(date) : Failed to execute SanityTest" python2.7 SanityCopyLogs.py ${TAG} $vmip || (echo "$(date) : Failed to copy logs" && exit -1)
cd ${WORKSPACE}
echo "###################################################################################################" echo "###################################################################################################" echo "###################################################################################################" echo "${date} : Done Sanity Tests" echo "###################################################################################################" echo "###################################################################################################" echo "###################################################################################################" else echo "$(date) : NOT running sanity tests on ovoc server" fi
################################################################################################### # Stop and Delete ovoc server vm SSHCMD="powershell -c 'Get-VM -VMName \"*ovoc*\" | %{Stop-VM \$_}'" echo $SSHCMD | ssh centos@${vmHostIP} SSHCMD="powershell -c 'Get-VM -VMName \"*ovoc*\" | %{Remove-VM -f \$_}'" echo $SSHCMD | ssh centos@${vmHostIP}
# Delete old remanents from hypervisor host SSHCMD="powershell -c 'Remove-Item -Force -Recurse V:\\HVWORKSPACE\\*'" echo $SSHCMD | ssh centos@${vmHostIP}
