hudson.plugins.parameterizedtrigger.Pred.properties:
REFSPEC=${REFSPEC}
GERRIT_BRANCH=${GERRIT_BRANCH}
EMAIL=${EMAIL}
BOARD=${BOARDNAME}_SIP.cmp
REFTARGET=${TARGET}
CHANGES=${CHANGESNumbers}

script.script:
artfcty_pat = ~/Deploying artifact: https:\/\/(.+\/)([^\/\s]+\b)/

// Adds artifacts from child build
def add_artifacts(artifacts, build) {
    // match "Deploying artifact: (.+)"
    def matches = build.logFile.text =~ artfcty_pat
    matches.each {
        if (!it[2].endsWith('.h')) // Ignore header files
            artifacts.appendText("<li><a href=\"https://sbc-read:sbc-read@${it[1]}${it[2]}\">${it[2]}</a></li>", false)
    }
}

def isMatrix = manager.buildIsA(hudson.matrix.MatrixBuild.class)
artifacts = manager.createSummary("package.png")
targetArtifactory="user/Firmware/" + manager.build.number
artifacts.appendText("<a href=\"https://sbc-read:sbc-read@artifactory/webapp/#/artifacts/browse/tree/General/sbc-review/com/ac/sbc/" + targetArtifactory + "\">Build Artifacts:</a><ul>", false)
// For matrix build, collect artifacts from all child builds. For normal build collect from self
if (isMatrix) {
    manager.build.exactRuns.each { run -> add_artifacts(artifacts, run) }
} else {
    add_artifacts(artifacts, manager.build);
}
artifacts.appendText("</ul>", false)
